import React, {useState} from 'react';
import AddUser from './Components/Users/AddUser';
import UsersList from './Components/Users/UsersList';
import classes from '../src/Components/Users/UsersList.module.css'
import Card from './Components/UI/Card';
import Button from './Components/UI/Button';
function App() {
  const [usersList, setUsersList] = useState([]);
  const addUserHandler =(uName, uAge) => {
    setUsersList((prevUsersList) => {
      return [...prevUsersList, {name: uName, age: uAge, id:Math.random().toString() }];
    })
  }
  const DeleteUser = (id) => {
    const newUsers = usersList.filter((user) => user.id !== id)
    setUsersList(newUsers)
  }
  const [nameChange, setName] = useState('')
  const EditUser = (id, name, age) =>{
    console.log(id, name, age)
        // if(id === id){
          nameChange = name
          setName('ttttt')
        // }
  }
  return (
    <div>
      <AddUser onAddUser={addUserHandler}></AddUser>
      <Card className={classes.users}>  
            <ul>
                {usersList.map((user) => 
                <li key={user.id}>
                        {user.name}  ({user.age} Years Old)
                        <Button onClick={() => DeleteUser(user.id)}>Delete User</Button>
                        <Button onClick={() => EditUser(user.id, user.name, user.age)}>Edit User</Button>
                        <form>
                          <input 
                            id='username' 
                            type="text" 
                            // value={enteredUsername}
                            // onChange={usernameChangeHandler}
                            > 
                          </input>
                          <input 
                            id='age' 
                            type="number" 
                            // value={enteredUsername}
                            // onChange={usernameChangeHandler}
                            > 
                          </input>
                        </form>
                </li>
                    )}
            </ul>
        </Card>
      {/* <UsersList users={usersList}></UsersList> */}
    </div>
  );
}

export default App;
